INSERT INTO `plants_db`.`fruit_trees` (`tree_id`,`common_name`,`cultivar`,`botanical_name`,`ripening_season`,`chill_min`,`chill_max`,`cold_hardiness`,`fruit`,`water_needs`,`sun`,`soil_type`,`ph_low`,`ph_high`,`fertilizer`,`originating_region`,`description`,`parentage`) VALUES (0,'Mango','Cogshall','Mangifera indica','June-July',null,null,null,'10-16 oz, sweet, aromatic, with a rich, spicy flavor',null,null,null,null,null,null,'Florida','\‘Fairchild\’ was selected by Dr. David Fairchild and his family in the early 1900s, in the Panama Canal Zone. The small, oblong fruit average 10 oz. and have lemon yellow skin at maturity in June and July. The juicy, fiber free flesh is deep orange and aromatic, with a rich, spicy flavor. \‘Fairchild\’ always ranks among the top cultivars in public evaluations at Fairchild\’s annual International Mango Festival. The tree and fruit are highly tolerant of disease and fruit well under humid conditions, making it a natural for South Florida. The tree is among the most ornamental of mangos, with its compact shape and deep green color. It can be maintained at a height and spread of 8 ft. or less, perfect for those with a modest-sized home garden.',null);
INSERT INTO `plants_db`.`fruit_trees` (`tree_id`,`common_name`,`cultivar`,`botanical_name`,`ripening_season`,`chill_min`,`chill_max`,`cold_hardiness`,`fruit`,`water_needs`,`sun`,`soil_type`,`ph_low`,`ph_high`,`fertilizer`,`originating_region`,`description`,`parentage`) VALUES (0,'Mango','Fairchild','Mangifera indica','June-July',null,null,null,'10 oz; firm, juicy, spicy flavor',null,null,null,null,null,null,'Panama','First one: ordered Sun 2/13/2011, paid $50, shipped Shipped Feb 22, 2011',null);
INSERT INTO `plants_db`.`fruit_trees` (`tree_id`,`common_name`,`cultivar`,`botanical_name`,`ripening_season`,`chill_min`,`chill_max`,`cold_hardiness`,`fruit`,`water_needs`,`sun`,`soil_type`,`ph_low`,`ph_high`,`fertilizer`,`originating_region`,`description`,`parentage`) VALUES (0,'Mango','Ice Cream','Mangifera indica','June - July',null,null,null,'small, 8 oz',null,null,null,null,null,null,null,'\‘Julie\’ is synonymous with the Caribbean. Selected in Jamaica, the fruit have a flattened oval shape and are small, generally weighing from 6 to 10 oz. The fruit are greenish to mustard yellow, with a pink blush when exposed to the sun. The deep orange, fiber-free flesh is soft and extremely juicy, with a rich and spicy flavor. The fruit are not stunning specimens, but they certainly have an extraordinary flavor. The fruiting season is from May to July, but unpredictable, with multiple crops throughout the summer. The tree is dwarf and should be grown in combination with other varieties to assure production.',null);
INSERT INTO `plants_db`.`fruit_trees` (`tree_id`,`common_name`,`cultivar`,`botanical_name`,`ripening_season`,`chill_min`,`chill_max`,`cold_hardiness`,`fruit`,`water_needs`,`sun`,`soil_type`,`ph_low`,`ph_high`,`fertilizer`,`originating_region`,`description`,`parentage`) VALUES (0,'Mango','Julie','Mangifera indica','June-July-August',null,null,null,'small 6-10 oz.',null,null,null,null,null,null,'Jamaica','\‘Keitt\’ are oval and quite large, weighing from 24 to 40 oz. Classified as a “green” for the commercial trade, it nonetheless can be a light yellow with a pink to rouge red blush on the sun-exposed shoulder of the fruit. The flesh is fiberless and is tangerine yellow, firm and quite juicy. The flavor is mild and sweet flavor with pleasant aromatic overtones. It is among the latest season mango and is highly tolerant of most diseases. The tree is easily manipulated by management and pruning and its size is influenced greatly by environment. \‘Keitt\’ is a perfect choice for the late season in the home garden. In the moist, hot tropics it grows to a medium or large tree, whereas in many desert climates it is a sprawling tree or near shrub that may be grown on trellis wires. The fruit are commercially sold as mature-green fruit for Asian and Latin markets and there is an increase in the use of \‘Keitt\’ for fresh cut due to the ability to cut with a bare minimum of maturity and maintain the “mango” flavor.',null);
