INSERT INTO `plants_db`.`fruit_trees` (`tree_id`,`common_name`,`cultivar`,`botanical_name`,`ripening_season`,`chill_min`,`chill_max`,`cold_hardiness`,`fruit`,`water_needs`,`sun`,`soil_type`,`ph_low`,`ph_high`,`fertilizer`,`originating_region`,`description`,`parentage`) VALUES (0,'Mango','Lancetilla','Mangifera indica','August-September',null,null,null,'large,up to five pound',null,null,null,null,null,null,'north coast of Honduras','\‘Neelum\’ is a popular South Indian dessert mango. The fruit weigh 9 to 12 oz., with the general shape of a fat cashew nut. They are smooth-skinned and bright yellow upon ripening and have no blush. The flesh is deep yellow or orange. There is no fiber and it has a rich, aromatic flavor perhaps a bit strong for the unaccustomed palate. \‘Neelum\’ is best eaten out-of-hand or used as slices or cubes in mixed fruit salads, as the firm flesh holds its shape. \‘Neelum\’ is a dwarf tree perfect for the home gardener, with a late fruiting season and excellent storage characteristics. Fruit should be harvested when mature green and ripened at room temperature off the tree.',null);
