INSERT INTO `plants_db`.`fruit_trees` (`tree_id`,`common_name`,`cultivar`,`botanical_name`,`ripening_season`,`chill_min`,`chill_max`,`cold_hardiness`,`fruit`,`water_needs`,`sun`,`soil_type`,`ph_low`,`ph_high`,`fertilizer`,`originating_region`,`description`,`parentage`) VALUES (0,'banana','Dwarf Namwah (Nam Weh) 6-8 ft.','Musa',null,null,null,null,null,null,null,null,null,null,null,null,'This dwarf banana from Thailand is the most commonly grown cultivar in that country. This delicious ladyfinger type dessert banana has been described as having the tastiest, sweetest, highest quality fruit.\nIt grows rapidly even in cool areas (hardy zones 9-10). The sweet, thin-skinned bananas mature very quickly, even when the flower appears somewhat late in the season. In addition, because it\'s a dwarf, it makes a great container banana. It is drought resistant and vigorous.',null);
INSERT INTO `plants_db`.`fruit_trees` (`tree_id`,`common_name`,`cultivar`,`botanical_name`,`ripening_season`,`chill_min`,`chill_max`,`cold_hardiness`,`fruit`,`water_needs`,`sun`,`soil_type`,`ph_low`,`ph_high`,`fertilizer`,`originating_region`,`description`,`parentage`) VALUES (0,'banana','Dwarf Red - 8 ft','Musa',null,null,null,null,null,null,null,null,null,null,null,null,'This very sweet lady finger fruit is the most beautiful. It turns \"sunset\" colors when ripening from dark burgundy to orange, yellow-green and muted colors in between. Mature height is only 8 ft. The bunch is compact, contains many medium size fruits, with thick peel, and flesh of strong flavor.',null);
