INSERT INTO `plants_db`.`fruit_trees` (`tree_id`,`common_name`,`cultivar`,`botanical_name`,`ripening_season`,`chill_min`,`chill_max`,`cold_hardiness`,`fruit`,`water_needs`,`sun`,`soil_type`,`ph_low`,`ph_high`,`fertilizer`,`originating_region`,`description`,`parentage`) VALUES (0,'banana','Ice Cream - 10-15 ft tall - died twice, suspected root rot','Musa',null,null,null,null,null,null,null,null,null,null,null,null,'Ice Cream (Blue Java) cultivar grows 10-15 ft tall. Produces bunches weighting 40-60 lbs. Stout, straight fruit, 5-7 inches long and up to 2 inches wide. Skin bluish with silvery bloom when young, pale-yellow when ripe. Flesh is white and sweet with vanilla-like flavor. Eaten raw or cooked.',null);
